y">				<?php if (isset($pageTerm->content)) echo $pageTerm->content; ?>				<?php				$text_date = '';				$date_now = date('d',strtotime(date('Y-m-d'))).'th of '.date('F Y',strtotime(date('Y-m-d')));				?>				<p>&nbsp;</p>			</div>		</div>	</div></div><script type="text/javascript">//<![CDATA[    $('.click-tearm-condition').on('click', function () {        var data=[];        var $el=$("#location_list_id");        $el.find('option:selected').each(function(){            data.push($(this).val());        });        var url_ = "<?php echo Yii::app()->createAbsoluteUrl('enquiry/getLocation'); ?>";        $.ajax({            url:url_,            data:{location_list_id:data, is_ajax:1},            type: "post",            dataType: "json",            async: false,            success: function (data) {                if (data['success'] == true) {                    $(".text-location").html(data['text']);                    $(".text-date").html('<?php echo $date_now;?>');                     $('#myModal').modal('show');                                   }else{                    return false;                }            }        });        return false;    });//]]></script>