           success: function (data) {                if (data['success'] == true) {                    $(".text-location").html(data['form-control']);                    $(".text-date").html('<?php echo $date_now;?>');                     $('#myModal-rent').modal('show');                                   }else{                    return false;                }            }        });        return false;    });//]]></script>