 & Conditions- applies to Seller and Landlord only.      *  to validate form client     * @param {boolean} hasError true neu co loi, false neu OK     * @returns {Boolean}     */    function fnAnhDungCheckSell(hasError){        var form = $('.GlobalEnquirySell').closest('form');        var term_check = form.find('.term_agree').is(':checked');        if( !term_check ){            form.find('.term_agree').closest('div').find('.errorMessage').show();        }else{            form.find('.term_agree').closest('div').find('.errorMessage').hide();        }        if((hasError==false) && term_check ){            return true;        }        return false;    }		$('.click-tearm-condition-sell').on('click', function () {        var data=[];        var $el=$("#ProGlobalEnquiry_location_id");        $el.find('option:selected').each(function(){            data.push($(this).val());        });        var url_ = "<?php echo Yii::app()->createAbsoluteUrl('enquiry/getLocation'); ?>";        $.ajax({            url:url_,            data:{location_list_id:data, is_ajax:1},            type: "post",            dataType: "json",            async: false,            success: function (data) {                if (data['success'] == true) {                    $(".text-location").html(data['form-control']);                    $(".text-date").html('<?php echo $date_now;?>');                     $('#myModal-sell').modal('show');                                   }else{                    return false;                }            }        });        return false;    });//]]></script>