<?php if($allBanner): ?>	<?php	$allImg = '';	if (count($allBanner) > 0 && is_array($allBanner)) {		foreach ($allBanner as $key => $banner) {			$i = $key + 1;			if($banner->link!=""){				$img = InputHelper::holderImage($banner->getImageUrl(728,90), 728, 90);				$allImg .= sprintf('<a href="%s" target="_blank">%s</a>', $banner->link, $img);			}else{				$allImg .= InputHelper::holderImage($banner->getImageUrl(728,90), 728, 90);;			}		}	} ?>    <div class="bn-advers bn-ads-bottom">		<div id="bn-list-bottom">			<?php echo $allImg ?>		</div>	</div>	<?php	Yii::app()->clientScript->registerScript('bottom-banner', "		$('#bn-list-bottom').bxSlider({			mode: 'fade',			auto: true,			pager: false,			controls: false,			pause: 15000,			preloadImages: 'visible'		});	"); ?>	<?php endif ?>