<div class="bn-small">    <div class="slider-ads">        <?php if ($banner): $allImg = ''; ?>            <?php foreach ($banner as $item): ?>                <?php                if ($item->link != "") {					$img = InputHelper::holderImage($item->getImageUrl(265,365), 265,365);					$allImg .= sprintf('<a href="%s" target="_blank">%s</a>', $item->link, $img);                } else {                    $allImg .= InputHelper::holderImage($item->getImageUrl(265,365), 265,365);                }                ?>            <?php endforeach; ?>            <?php echo $allImg ?>					<?php 			Yii::app()->clientScript->registerScript('right-banner', "				$('.slider-ads').bxSlider({					mode: 'fade',					auto: true,					pager: false,					controls: false,					pause: 15000				});			");			?>		<?php endif; ?>    </div></div>