<?php/* @var $banner Banner */?><?php if ($banner): ?>	<?php	Yii::app()->clientScript->registerScript('top-banner', "		$('#bn-list').bxSlider({			mode: 'fade',			auto: true,			pager: false,			controls: false,			pause: 3000,			preloadImages: 'visible'		});	");		$Allbanner = Banners::getAdsBannerByType(TOP);	$allImg = '';	if (count($Allbanner) > 0 && is_array($Allbanner)) {		foreach ($Allbanner as $key => $banner) {			$i = $key + 1;			$img = InputHelper::holderImage($banner->getImageUrl2(728,90), 728, 90);			$allImg .= sprintf('<a href="%s" target="_blank">%s</a>', $banner->link, $img);		}	} ?>    <div class="bn-advers">			<div id="bn-list"><?php echo $allImg ?></div>             </div> <?php endif ?>