<?php if ($banner): ?>    <div id="banner-slider">        <?php        foreach ($banner as $item) {            $allImg = "";            if ($item->link != "") {				$img = InputHelper::holderImage($item->getSlideshowImageUrl(1519, 350), 1519, 350);				$allImg .= sprintf('<a href="%s" target="_blank">%s</a>', $item->link, $img);            } else {				$allImg .= InputHelper::holderImage($item->getSlideshowImageUrl(1519, 350), 1519, 350);            }            echo $allImg;        }        ?>    </div>	<?php 	Yii::app()->clientScript->registerScript('homepage-banner', "		$('#banner-slider').bxSlider({ 			pager: true,			controls: false,			auto: true,			mode: 'fade',			pause: 10000,			preloadImages: 'visible'		});	");	?><?php endif ?>