 });    });    $('#BankRequest_tenancy_expiry_date_1').change(function () {        var div = $(this).closest('div.group');        div.find('.hasDatepicker').val('');    });    $('#BankRequest_tenancy_expiry_datepicker').change(function () {        $('#BankRequest_tenancy_expiry_date_0').attr('checked', true);        $('#BankRequest_tenancy_expiry_date_0').parent('span').addClass('checked');        $('#BankRequest_tenancy_expiry_date_1').attr('checked', false);        $('#BankRequest_tenancy_expiry_date_1').parent('span').removeClass('checked');    });    $('#BankRequest_type_selling_1, #BankRequest_type_selling_2').change(function () {        var divTenancy = $('#tenancy-content-bankrequest');        $('#tenancy-content-bankrequest input:radio').each(function () {            $(this).attr('checked', false);            $(this).parent('span').removeClass('checked');        });        $('#tenancy-content-bankrequest input').val('');        $('#tenancy-content-bankrequest textarea').val('');        $('#tenancy-content-bankrequest select').val('Unfurnished').trigger('change');    });</script>  