ldModel->pass_expiry_date;             $old_scanned_passport = $OldModel->scanned_passport;             $old_upload_employment_pass_passport = $OldModel->upload_employment_pass_passport;         }        //        if ( ($this->id_type == Users::ID_TYPE_EP || $this->id_type == Users::ID_TYPE_SPASS )) {        if ( $this->id_type != '' && ( !in_array($this->id_type, Users::$ARR_ID_NOT_REQUIRED) )) {            if((trim($this->pass_expiry_date)=='' || $this->pass_expiry_date=='0000-00-00' || $this->pass_expiry_date==NULL )){                $label = $this->getAttributeLabel('pass_expiry_date');                $this->addError("pass_expiry_date", "$label  cannot be blank.");            }                        if(empty($this->scanned_passport)  && trim($old_scanned_passport) == "" ){                $label = $this->getAttributeLabel('scanned_passport');                $this->addError("scanned_passport", "$label  cannot be blank.");            }                        if(empty($this->upload_employment_pass_passport)  && trim($old_upload_employment_pass_passport) == "" ){                $label = $this->getAttributeLabel('upload_employment_pass_passport');                $this->addError("upload_employment_pass_passport", "$label  cannot be blank.");            }        }    }          /**     * @return array customized attribute labels (name=>label)     */    public function attributeLabels() {        $return =  array(            'id' => 'ID',            'email' => 'Email',            'email_not_login' => 'Email',            'email_confirm' => 'Confirm email',            'password_hash' => 'Password',            'password_confirm' => 'Confirm Password',            'temp_password' => 'Temp Password',            'first_name' => 'First Name',            'last_name' => 'Last Name',            'first_char' => 'First Char',            'login_attemp' => 'Login Attemp',            'created_date' => 'Created Date',            'last_logged_in' => 'Last Logged In',            'ip_address' => 'Ip Address',            'role_id' => 'Role',            'application_id' => 'Application',            'status' => 'Status',            'gender' => 'Gender',            'phone' => 'Phone',            'verify_code' => 'Verify Code',            'full_name' => 'Name',            'address' => 'Address',            'area_code_id' => 'Area Code',            'currentpassword' => 'Current Password',            'newpassword' => 'New password',            'company_name' => 'Company Name',            'nric_passportno_roc' => 'NRIC/FIN/PP No',            'contact_no' => 'Contact No',            'postal_code' => 'Postal Code',            'id_type' => 'ID Type',            'country_id' => 'Country',            'is_subscriber' => 'Subscriber Newsletter',            'TierManagerId' => 'Tier Manager',            'upload_employment_pass_passport' => 'Employment Pass',            'scanned_passport' => 'Scanned Passport',            'commission_schema_id' => 'Commission Scheme',            'agent_cea' => 'CEA',            'agent_company_name' => 'Company Name',            'agent_company_logo' => 'Company Logo',            'ProAgentDistrict' => 'Access Districts',            'gst' => 'GST',            'expiration_date' => 'Tenancy Expiration Date',            'view_count' => 'Number of Views',        );                $aCheckName = array('create_landlord','update_landlord','index_landlord','view_landlord',                'create_tenant','update_tenant','index_tenant','view_tenant', 'myprofile_tenant','myprofile_admin'            );        $aCheckAddress = array('create_landlord','update_landlord','index_landlord',            'view_landlord',            );        $aCheckTenant = array(                        'create_tenant','update_tenant','index_tenant','view_tenant',            );                        if(in_array($this->scenario, $aCheckName)){            $return['first_name'] = "Full Name";        }        if(in_array($this->scenario, $aCheckAddress)){            $return['address'] = "Correspondence Address";        }        if(in_array($this->scenario, $aCheckTenant)){            $return['address'] = "Residential Address";        }                return $return;    }    public function compareEmail($attribute, $params) {        if ($this->email_confirm != $this->email) {            $this->addError("email_confirm", "$this->email_confirm email is wrong.");        }    }    //bb    public function checkCurrentPassword($attribute, $params) {        if (!$this->hasErrors()) {                            if ($this->password_hash != md5(trim($this->currentpassword))) {                    $this->addError('currentpassword', 'Current password is wrong.');                }        }    }        public function comparePassword($attribute, $params) {        if (!$this->hasErrors()) {            if (trim($this->currentpassword) == '')                $this->addError('currentpassword', 'Current password cannot be blank.');            else {                //$tempPass = $this->currentpassword;                //$this->currentpassword = md5(trim($this->currentpassword));                if ($this->md5pass != md5(trim($this->currentpassword))) {                    $this->addError('currentpassword', 'Current password wrong.');                }                //$this->currentpassword = $tempPass;            }        }    }    public function search($criteria = NULL) {        if ($criteria == NULL)            $criteria = new CDbCriteria;        $criteria->compare('t.email', $this->email, true);        $criteria->compare('t.username', $this->username, true);        $criteria->compare('t.first_name', $this->first_name, true);        $criteria->compare('t.last_name', $this->last_name, true);        $criteria->compare('CONCAT(t.first_name, t.last_name)', $this->full_name, true);        $criteria->compare('t.created_date', $this->created_date, true);        $criteria->compare('t.ip_address', $this->ip_address, true);//		$criteria->compare('t.role_id',$this->role_id);        $criteria->addCondition('t.role_id <> 2');        $criteria->compare('t.application_id', $this->application_id);        $criteria->compare('t.status', $this->status);        $criteria->compare('t.phone', $this->phone, true);        //		$criteria->order = "t.first_name asc, t.last_name asc";        $criteria->order = "t.created_date desc";        /**         * <Jason>         * <Date: 20131002>         * <To export excel for all members>         */        $_SESSION['data_user-excel'] // for retrive all items                = new CActiveDataProvider($this, array(            'pagination' => false,            'criteria' => $criteria,        ));        return new CActiveDataProvider($this, array(            'criteria' => $criteria,             'pagination'=>array(                'pageSize'=> Yii::app()->user->getState('pageSize',Yii::app()->params['defaultPageSize']),            ),        ));    }        /**     * @Author: ANH DUNG Apr 02, 2014     * @Todo: search user Landlord, tenant, register user, agent at BE     */        public function searchForProperty($criteria = NULL) {        if ($criteria == NULL)            $criteria = new CDbCriteria;        $criteria->compare('t.first_name', $this->first_name, true);        $criteria->compare('t.last_name', $this->last_name, true);        $criteria->compare('t.role_id',$this->role_id);        $criteria->compare('t.application_id', $this->application_id);        $criteria->compare('CONCAT(t.first_name," ", t.last_name)', $this->full_name, true);        $criteria->compare('t.status', $this->status);        $criteria->compare('t.address', $this->phone, true);        $criteria->compare('t.email_not_login', $this->email_not_login, true);        $criteria->compare('t.contact_no', $this->contact_no, true);        $criteria->compare('t.id_type', $this->id_type);        $criteria->compare('t.nric_passportno_roc', $this->nric_passportno_roc, true);//        $criteria->order = "t.id desc";        $sort = new CSort();        $sort->attributes = array(            'id'=>'id',            'first_name'=>'first_name',            'full_name' => array(                'asc' => 'CONCAT(first_name, last_name)',                'desc' => 'CONCAT(first_name, last_name) desc',            ),            'nric_passportno_roc'=>'nric_passportno_roc',            'id_type'=>'id_type',            'email'=>'email',            'email_not_login'=>'email_not_login',            'address'=>'address',            'status'=>'status',            'gst'=>'gst',            'contact_no'=>'contact_no',                        'email_click'=>'email_click',                        'phone_click'=>'phone_click',                        'view_count'=>'view_count',                        );                $sort->defaultOrder = 't.id DESC';                  $_SESSION['data_user-excel'] // for retrive all items                = new CActiveDataProvider($this, array(            'pagination' => false,            'criteria' => $criteria,                    'sort' => $sort,        ));        return new CActiveDataProvider($this, array(            'criteria' => $criteria,             'pagination'=>array(                'pageSize'=> Yii::app()->user->getState('pageSize',Yii::app()->params['defaultPageSize']),            ),            'sort' => $sort,                    ));    }    public function searchAdmin($criteria = NULL) {        if ($criteria == NULL)            $criteria = new CDbCriteria;        $criteria->compare('t.username', $this->username, true);        $criteria->compare('t.email', $this->email, true);        $criteria->compare('t.first_name', $this->first_name, true);        $criteria->compare('t.last_name', $this->last_name, true);        $criteria->compare('t.created_date', $this->created_date, true);        $criteria->compare('t.role_id', $this->role_id);        $criteria->compare('t.application_id', 1);        $criteria->compare('t.status', $this->status);        $criteria->compare('t.phone', $this->phone, true);        return new CActiveDataProvider($this, array(            'criteria' => $criteria,        ));    }        public function searchSubAdmin($criteria = NULL) {        if ($criteria == NULL)            $criteria = new CDbCriteria;        $criteria->compare('t.username', $this->username, true);        $criteria->compare('t.email', $this->email, true);        $criteria->compare('t.first_name', $this->first_name, true);        $criteria->compare('t.last_name', $this->last_name, true);        $criteria->compare('t.created_date', $this->created_date, true);        $criteria->compare('t.role_id', $this->role_id);        $criteria->compare('t.application_id', 1);//        $criteria->addCondition('t.role_id<>2');        $criteria->addNotInCondition('t.role_id', array(ROLE_MANAGER, ROLE_ADMIN));        $criteria->compare('t.status', $this->status);        $criteria->compare('t.phone', $this->phone, true);        return new CActiveDataProvider($this, array(            'criteria' => $criteria,        ));    }    public function defaultScope() {        return array(                //'condition'=>'',        );    }    public function unlinkAllFileInFolder($path) {        $files = glob($path . '/*'); // get all file names        foreach ($files as $file) { // iterate files            if (is_file($file))                unlink($file); // delete file        }    }    public function beforeDelete() {        try {            self::removeFile($t