me = $mProTransactionsVendorPurchaserDetail->scanned_employment_pass;            $url_download = Yii::app()->createAbsoluteUrl('/')."/$fileFolder/$fileModelId/$fileName";            MyFormat::DownloadFile($url_download, $pathSaveFile, $fileName);            $mUser->upload_employment_pass_passport = $fileName;        }        if(!empty($mProTransactionsVendorPurchaserDetail->scanned_passport)){            $fileName = $mProTransactionsVendorPurchaserDetail->scanned_passport;            $url_download = Yii::app()->createAbsoluteUrl('/')."/$fileFolder/$fileModelId/$fileName";            MyFormat::DownloadFile($url_download, $pathSaveFile, $fileName);            $mUser->scanned_passport = $fileName;                    }        $mUser->update(array('upload_employment_pass_passport', 'scanned_passport'));                }            /**     * @Author: ANH DUNG Apr 24, 2014     * @Todo: copy two file upload of user tenant to table ProTransactionsVendorPurchaserDetail of transaction     * @Param: $mUser model user     * @Param: $mProTransactionsVendorPurchaserDetail model ProTransactionsVendorPurchaserDetail     * two field: scanned_employment_pass, scanned_passport     */    public static function copyTwoFileFromUserToTenant($mUser, $mProTransactionsVendorPurchaserDetail){        $fileFolder = ProTransactionsVendorPurchaserDetail::$folderUpload;        $fileModelId = $mProTransactionsVendorPurchaserDetail->id;        $imageProcessing = new ImageProcessing();        $imageProcessing->createDirectoryByPath($fileFolder.'/'.$fileModelId);        $pathSaveFile = "/".$fileFolder."/$fileModelId";        $UserFolder = self::$folderUpload;        $Uid = $mUser->id;        if(!empty($mUser->upload_employment_pass_passport)){            $fileName = $mUser->upload_employment_pass_passport;            $url_download = Yii::app()->createAbsoluteUrl('/')."/$UserFolder/$Uid/$fileName";            MyFormat::DownloadFile($url_download, $pathSaveFile, $fileName);            $mProTransactionsVendorPurchaserDetail->scanned_employment_pass = $fileName;        }        if(!empty($mUser->scanned_passport)){            $fileName = $mUser->scanned_passport;            $url_download = Yii::app()->createAbsoluteUrl('/')."/$UserFolder/$Uid/$fileName";            MyFormat::DownloadFile($url_download, $pathSaveFile, $fileName);            $mProTransactionsVendorPurchaserDetail->scanned_passport = $fileName;                    }        $mProTransactionsVendorPurchaserDetail->update(array('scanned_employment_pass', 'scanned_passport'));                }                 /**     * Apr 01, 2014 - ANH DUNG     * To do: save file      * @param: $model model user     * @param: $nameField ex: file_name     * @param: $pathUpload ex: 'upload/members'       * @return: name of image     */    public static function  saveFile($model, $nameField, $pathUpload, $count)    {        if(is_null($model->$nameField)) return '';            $ext = $model->$nameField->getExtensionName();        $fileName = MyFunctionCustom::slugify($model->$nameField->getName());        $fileName = str_replace(strtolower($ext), '', $fileName);        $fileName = trim($fileName, '-');        $fileName = trim($fileName);        $fileName = $fileName.'-'.time().$count.'.'.$ext;//        $fileName = $fileName.'.'.$ext;        $imageProcessing = new ImageProcessing();        $imageProcessing->createDirectoryByPath($pathUpload.'/'.$model->id);        $model->$nameField->saveAs($pathUpload.'/'.$model->id.'/'.$fileName);        return $fileName;    }                 /**     * Apr 01, 2014 - ANH DUNG     * To do: delete file  of model user     * @param: $model Users     * @param: $nameField ex: upload_employment_pass_passport     * @param: $pathUpload ex: 'upload/members'       */        public static function deleteOldFile($model, $nameField, $pathUpload)    {        $modelDel = self::model()->findByPk($model->id);        if(is_null($modelDel) || empty($modelDel->$nameField))return;        self::removeFile($modelDel, $nameField, $pathUpload);    }             /**     * @Author: ANH DUNG  Apr 02, 2014     * @Todo: only remove file of user     * @Param: $modelDel is model Users          * @Param: $nameField field file in model user     */    public static function removeFile($modelDel, $nameField, $pathUpload){        if(trim($modelDel->$nameField)=='') return;        $ImageProcessing = new ImageProcessing();        $ImageProcessing->folder = '/'.$pathUpload.'/'.$modelDel->id;        $ImageProcessing->delete($ImageProcessing->folder.'/'.$modelDel->$nameField);            }                public function getInforUser($id = null, $name = null) {        /**         * Get information user         */        $id = (int) $id;        $name = trim($name);        if (empty($id))            return;        if (!empty($name))            $result = Users::model()->findByPk($id)->$name;        else            $result = Users::model()->findByPk($id);        return $result;    }    public static function loadItems($emptyOption = false) {        $_items = array();        if ($emptyOption)            $_items[""] = "";        $model = self::model()->findByPk(Yii::app()->user->getId());        //foreach($models as $model)        $_items[$model->id] = $model->email;        return $_items;    }    public static function generateKey($user) {        if (empty($user->email))            $user->email = '';        return md5($user->id . $user->email);    }    public static function findByVerifyCode($verify_code) {        return Users::model()->find('verify_code=' . $verify_code . '');    }    public function checkDigit($attribute, $params) {        if ($this->password_hash != '') {//            $containsDigit   = preg_match('/\d/',$this->password_hash);            $containsDigit = preg_match('/[A-Za-z].*[0-9]|[0-9].*[A-Za-z]/', $this->password_hash);            $lb = $this->getAttributeLabel('password_hash');            if (!$containsDigit)                $this->addError("password_hash", "$lb must be at least one letter and one number.");        }    }    public function checkDigit_new($attribute, $params) {        if ($this->newpassword != '') {//            $containsDigit   = preg_match('/\d/',$this->password_hash);            $containsDigit = preg_match('/[A-Za-z].*[0-9]|[0-9].*[A-Za-z]/', $this->newpassword);            $lb = $this->getAttributeLabel('newpassword');            if (!$containsDigit)                $this->addError("newpassword", "$lb must be at least one letter and one number.");        }    }                /**     * @Author: ANH DUNG Apr 25, 2014     * @Todo: validate email of landlord and tenant     * @Param: $model  model ProTransactionsVendorPurchaserDetail     */        public static function validateDefaultTenant($model){        if(empty($model->user_id)){            $mUser = new Users('AgentAddLandlord');            $mUser->email_not_login = $model->email;            $mUser->nric_passportno_roc = $model->nric_passportno_roc;            $mUser->validate(array('email_not_login','nric_passportno_roc'));                        if($mUser->hasErrors()){                if( trim($mUser->email_not_login) != '' && $mUser->getError('email_not_login')){                    $model->addError('email', $mUser->getError('email_not_login'));                }                if($mUser->getError('nric_passportno_roc')){                    $model->addError('nric_passportno_roc', $mUser->getError('nric_passportno_roc'));                }            }        }    }        /**     * @Author: ANH DUNG Apr 25, 2014     * @Todo: validate email of landlord and tenant     * @Param: $model  model ProTransactionsVendorPurchaserDetail     */        public static function validateEmailLandlordTenant($model){        $mUser = new Users('AgentAddLandlord');        if(!empty($model->user_id))            $mUser->id = $model->user_id;        $mUser->email_not_login = $model->email;        $mUser->nric_passportno_roc = $model->nric_passportno_roc;        $mUser->validate(array('email_not_login','nric_passportno_roc'));                    if($mUser->hasErrors()){            if($mUser->getError('email_not_login')){//                $model->addError('email', $mUser->getError('email_not_login'));            }            if($mUser->getError('nric_passportno_roc')){                $model->addError('nric_passportno_roc', $mUser->getError('nric_passportno_roc'));            }        }    }        /**     * @Author: ANH DUNG Mar 28, 2014     * @Todo: save user form model ProTransactionsVendorPurchaserDetail     * @Param: $model is model ProTransactionsVendorPurchaserDetail     * @Return: model user     */    public static function saveUserVendorPurchaser($model, $role_id){        $mUser = new Users();        $mUser->scenario = 'abcdef';        $mUser->first_name = $model->name;        $mUser->email_not_login = $model->email;        $mUser->nric_passportno_roc = $model->nric_passportno_roc;        $mUser->contact_no = $model->contact_no;        $mUser->address = $model->address;        $mUser->postal_code = $model->postal_code;        $mUser->pass_expiry_date = $model->pass_expiry_date;        $mUser->id_type = $model->id_type;//        $mUser->role_id = $role_id;        $mUser->status = STATUS_ACTIVE;        $mUser->application_id = FE;        $mUser->temp_password = ActiveRecord::randString();        $mUser->password_hash = md5($mUser->temp_password);        $mUser->save();        return $mUser;    }            public static function saveUserExternalCoBroke($model, $role_id){        $mUser = null;  